package einheit1;
import java.util.Scanner;

public class Multiplizieren {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		Scanner scan = new Scanner (System.in);
		
		System.out.println("Bitte erste Zahl");
		int z1 = scan.nextInt();
		
		
		
		System.out.println("Bitte zweite Zahl");
		int z2 = scan.nextInt();
		
		System.out.println("Das Ergebnis " + z1 + " und der Zahl " + z2 + " lautet: " + (z1+z2));
		
		
		
		
		
		
		
		
		
		
	}

}
